package MakeAss;

use strict;
use warnings;

use Data::Dumper;
use POSIX;

use Encode; #help avoid wide print errors
use utf8;

sub writeass
{
  my $tmp = shift;
  my @codes = @_;

  open(my $fh, ">:encoding(utf8)", sprintf("%s/output.ass", $tmp));
  print $fh <DATA>;
  close($fh);
}

